mapper.update:
UPDATE product
SET 
productName=#{productName},
brand=#{brand},
content=#{content},
price=#{price}
saveFileName =#{saveFileName}
fileSize = #{fileSize}
WHERE productNum = #{productNum}

UPDATE product
SET viewCount = viewCount+1
WHERE productNum=#{productNum}

mapper.select:
   SELECT *
FROM
   (SELECT result1.*, ROWNUM rnum
   FROM (productNum,productName,brand,content,price,,
        saveFileName
        FROM product
        ORDER BY productNum DESC
        ) result1
           )
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
